import {useEffect, useState} from 'react'
import axios from 'axios'
import Chat from '../components/Chat'
import ChatInput from '../components/ChatInput'
const ChatDisplay = ({user,clickedUser}) => {

	const userId = user?.user_id
	const crosspondingUserId = clickedUser?.user_id
	const [userMessages , setUserMessages] = useState(null)
	const [clickedUserMessage,setClickedUserMessage] = useState(null)
	const getMessages = async (senderId,recipientsId)=>{
		try{
			const response = await axios.get('http://localhost:8000/users/messages',{
				params:{userId:senderId,crosspondingUserId:recipientsId}
			})
			setUserMessages(response.data)
		}
		catch(err){
			console.log(err)
		}
	}


    useEffect(()=>{
       getMessages(userId,crosspondingUserId)
	   getMessages(clickedUser,userId)
	},[])


	return (
	  <>	
	  <Chat/>
	  <ChatInput />
	  </>
	)
}

export default ChatDisplay
