import TinderCard from 'react-tinder-card'
import { useEffect, useState } from 'react'
import ChatContainer from '../components/ChatContainer'
import axios from 'axios'
import {useCookies} from 'react-cookie'
const Dashboard = () => {

    const [user, setUser] = useState(null)
    const [genderedUser,setGenderedUser]=useState(null)
    const [lastDirection, setLastDirection] = useState()
	  const [cookies,setCookie,removeCookie] = useCookies(['user'])
   	const userId=cookies.userID

    const getUser=async()=>{
	  try{
	    const response = await axios.get('http://localhost:8000/user',{
		  params:{userId}
		})
	    setUser(response.data)
	  }
	  catch(err){
	    console.log(err)
	  }
	}

  const getGenderedUser= async()=>{
    try{
      const response=await axios.get('http://localhost:8000/gendered_user',{
        params:{gender:user?.gender_interest}
      })
      setGenderedUser(response.data)
    }
    catch(error){
      console.log(error)
    }
  }



	useEffect(()=>{
	getUser()
  getGenderedUser()
	},[user,genderedUser])


const db = [
  {
    name: 'Richard Hendricks',
    url: 'https://i.imgur.com/Cp5V7XL.jpeg'
  },
  {
    name: 'Erlich Bachman',
    url: 'https://i.imgur.com/Cp5V7XL.jpeg'
  },
  {
    name: 'Monica Hall',
    url: 'https://i.imgur.com/Cp5V7XL.jpeg'
  },
  {
    name: 'Jared Dunn',
    url: 'https://i.imgur.com/Cp5V7XL.jpeg'
  },
  {
    name: 'Dinesh Chugtai',
    url: 'https://i.imgur.com/Cp5V7XL.jpeg'
  }
]
  const characters = db

  const swiped = (direction, nameToDelete) => {
    console.log('removing: ' + nameToDelete)
    setLastDirection(direction)
  }

  const outOfFrame = (name) => {
    console.log(name + ' left the screen!')
  }


	return (	 
    <>
    {user &&
		<div className="dashboard">
		  <ChatContainer user={user} />
			<div className="swipe-container">
			<div className="card-container">
	        {genderedUser.map((character) =>
          <TinderCard 
			className='swipe' 
			key={character.name} 
			onSwipe={(dir) => swiped(dir, character.name)} 
			onCardLeftScreen={() => outOfFrame(character.name)}>

            <div style={{ backgroundImage: 'url(' + character.url + ')' }} className='card'>
              <h3>{character.name}</h3>
            </div>
          </TinderCard>
        )}
			<div className="swipe-info">
			 {lastDirection && <p>You swiped {lastDirection} </p> }
			</div>
			</div>
			</div>

		</div>
    }
    </>
	)
}

export default Dashboard
