import {useEffect, useState} from 'react'
import axios from 'axios'
import Chat from '../components/Chat'
import ChatInput from '../components/ChatInput'
const ChatDisplay = ({user,clickedUser}) => {

	const userId = user?.user_id
	const crosspondingUserId = clickedUser?.user_id
	const [userMessages , setUserMessages] = useState(null)
	const [clickedUserMessage,setClickedUserMessage] = useState()
	const getUserMessage = async ()=>{
		try{
			const response = await axios.get('http://localhost:8000/users/messages',{
				params:{userId:userId,crosspondingUserId:crosspondingUserId}
			})
			setUserMessages(response.data)
		}
		catch(err){
			console.log(err)
		}
	}
	const getClickedUserMessage = async ()=>{
		try{
			const response = await axios.get('http://localhost:8000/users/messages',{
				params:{userId:crosspondingUserId,crosspondingUserId:userId}
			})
			setUserMessages(response.data)
		}
		catch(err){
			console.log(err)
		}
	}

    useEffect(()=>{
       getUserMessage()
	   getClickedUserMessage()
	},[])

	console.log(userMessages)

	return (
	  <>	
	  <Chat  userMessage={userMessages} />
	  <ChatInput />
	  </>
	)
}

export default ChatDisplay
